import { API_URL } from "../utils/constants";

export async function deletePersonApi(data:any, newValues:any, token:any) {
    const {id:groupId, people} = data;
    console.log(people,groupId,  'desde el api');

    let oldValues = people.map((person:any) =>  person.id)
    try {
      const url = `${API_URL}group/manage-members`;
      const params = {
        method: "POST",
        headers: {
          Authorization: `${token.token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({groupId, oldValues, newValues}),
      };
      const response = await fetch(url, params);
      const result = await response.json();
      console.log(result)
      return result;
    } catch (error) {
      throw error;
    }
  }

